@import 'colors';
@import 'fonts';
@import 'animation';
@import 'scrollmagic';

body {
  margin: 0;
  box-sizing: border-box;
}

// GENERAL CSS
.practice {
  height: 200vh;
}

.chapter-trigger {
  height: 5vh;
}

.section-decease {
  height: 100vh;
  padding: 5vh 5vw;
  width: calc(100% - 10vw);
  overflow: hidden;

  .header {
    z-index: 10;
    position: relative;
  }

  p {
    margin-top: 10px;
    color: $black;
  }

  .letter {
    position: relative;
    display: inline-block;
    color: $red;
  }

  .body-text {
    display: block;
  }

  .container {
    max-width: 1280px;
    margin: 0 auto;

    .flex {
    padding: 10vh 0;
    display: flex;
    flex-wrap: nowrap;
    justify-content: space-between;
    align-items: flex-start;

      .text-block {
        width: 50%;
        display: flex;
        flex-direction: column;
        justify-content: flex-start;
        z-index: 10;
      }

      .animation {
        width: 50%;
        height: 400px;
        position: relative;
        display: flex;
        flex-direction: column;
        justify-content: center;
        z-index: 0;

        .text-block {
          width: 100%;
          opacity: 0;

          .chance {
            display: flex;
            align-items: flex-start;

            h2 {
              background-color: $black;
              padding: 0 10px 3px 10px;
              margin-left: 10px;
              color: $red;
            }

            p {
              font-weight: 900;
              transform: translateY(4px);
              color: $red;
            }
          }

          .explain-text {
            max-width: 400px;
            margin-right: 0;
          }
        }
      }
    }
  }
}

.chapter-container {
  min-height: 100vh;
  width: 100vw;
  overflow: hidden;
  background: $brokenwhite;
  position: relative;

  .chapter {
    position: relative;
    overflow: hidden;
    padding: 10px 5vw;
    height: 80px;
    width: calc(100% - 10vw);
    font-family: barlow-bold;
    color: $red;
    z-index: 10;

    .chapter-appear {
      position: absolute;
      padding: 10px 5vw;
      width: calc(100% - 10vw);
      z-index: 99;
      top: 100px;
      left: 0;
    }
  }

  .count {
    position: fixed;
    top: -50px;
    right: 50px;
    z-index: 9;

    span {
      color: $red;
      opacity: 0.1;
      position: relative;
      user-select: none;
    }

    span:first-of-type {
      left: 30px;
      font-size: 14em;
    }

    span:nth-of-type(2) {
      top: 50px;
      margin: 0;
      font-size: 8em;
      position: relative;
    }

    span:last-of-type {
      top: 53px;
      right: 10px;
      font-size: 5em;
      position: relative;
    }
  }
}

.point {
  fill: $orange;
}
