.form-container {
  max-width:400px;
  width:100%;
  margin:0 auto;
  position:relative;

  #contact {
    position: relative;
    padding: 0 25px 25px 25px;
    background: #f3f2f0;

    &::after {
      content: '';
      position: absolute;
      top: 0; right: 0;
      border-top: 40px solid $salmon;
      border-left: 40px solid #f3f2f0;
      width: 0;
    }

    input[type="text"], input[type="email"], input[type="tel"], input[type="url"], textarea, button[type="submit"] {
     font-family: arial, sans-serif;
     font-size: 1em;
     color: $black;
    }

    h2 {
      transform: translateY(-32px);
      color: $black;
    }

    h4 {
      margin:5px 0 15px;
      display:block;
      font-size:13px;
    }

    fieldset {
      border: medium none !important;
      margin: 0 0 10px;
      min-width: 100%;
      padding: 0;
      width: 100%;
    }

    input[type="text"], input[type="email"], input[type="tel"], input[type="url"], textarea {
      width: calc(100% - 25px);
      border: 2px solid $white;
      background: $white;
      margin: 0 0 5px;
      padding: 10px;
      transition: all .2s;
    }

    textarea {
      height:100px;
      max-width:100%;
      resize:none;
    }

    input:focus, textarea:focus {
      outline:0;
      border: 2px solid $black;
    }

    button[type="button"] {
      font-family: barlow-bold, arial, sans-serif;
      cursor: pointer;
      width: 100%;
      border: none;
      background: $salmon;
      color: #FFF;
      margin:0 0 5px;
      padding:10px;
      font-size: 1em;

      &:hover {
        background: $black;
        -webkit-transition:background 0.3s ease-in-out;
        -moz-transition:background 0.3s ease-in-out;
        transition:background-color 0.3s ease-in-out;
      }

      &:active {
        box-shadow:inset 0 1px 3px rgba(0, 0, 0, 0.5);
      }

    }
  }
}

