// LUNG CSS
.lung {
  min-height: 100vh;

  .container {
    max-width: 200vw;
    width: calc(200vw - 10vw);

    .flex {
      .text-block-container {
        width: 50%;
        display: flex;
        flex-direction: column;
        justify-content: flex-start;
        align-items: center;
        z-index: 10;
      }

      .animation {
        width: 50%;
        position: relative;
        display: flex;
        flex-direction: column;
        justify-content: center;
        z-index: 0;

          .text-block-lung {
            padding-left: 5vw;
            width: 50%;

            .chance {
              p {
                color: $black;
              }
            }
          }

          .lungs-container {
            width: 500px;
            height: 600px;
            position: absolute;
            left: 50%;
            top: 50%;
            transform: translate(-50%, -50%);
          }

          .lung {
            position: absolute;
            left: 0;
            top: 0;
            z-index: 9;
            opacity: 1;
          }

           .lung-flat {
            position: absolute;
            left: 0;
            top: 0;
            z-index: -1;
          }

          .no2 {
            position: absolute;
            top: 0;
            left: 0;
            opacity: 0;

            .n {
              position: absolute;
              top: 0px;
              left: 165px;
            }

            .n p {
              font-size: 2em;
              font-weight: bold;
            }

            .streep1 {
                position: absolute;
                top: 65px;
                left: 150px;
                transform: rotate(40deg);
                border-left: 3px solid black;
                height: 60px
            }

            .streep2 {
                position: absolute;
                top: 65px;
                left: 195px;
                transform: rotate(-40deg);
                border-left: 3px solid black;
                height: 60px
            }
            .streep2:before {
              content: " ";
              position: absolute;
              z-index: -1;
              top: 2px;
              left: 3px;
              right: 5px;
              bottom: -2px;
              border: 1.5px solid black;
            }

            .o1, .o2 {
              position: absolute;
              top: 130px;
              width: 20px;
              height: 20px;
              border-style: solid ;
              border-color: black;
              border-width: 4px;
              border-radius: 50%;
            }

            .o1 {
              left: 100px;
            }

             .o2 {
              left: 225px;
            }

            .o {
              position: absolute;
            }
          }

          // https://codepen.io/MrDoctype/pen/OavrKB
          #bubble {
            position:absolute;
            top: 100px;
            right: 100px;
            width: 300px;
            height: 300px;
            margin: 0 auto;
            background: linear-gradient(to left, #EEE8D7, #FFFFFF);
            border-radius: 52% 48% 70% 30% / 41% 47% 53% 59%;
            border: solid 5px black;
            animation: bubble ease-in-out infinite;
            opacity: 0;
            transition: all .5s ease-in-out;

            &.not-infected {
              background: linear-gradient(to left, #EEE8D7, #FFFFFF);
              animation-duration: 12s;
            }

            &.infected {
              background: linear-gradient(to left, #870000, #190A05);
              animation-duration: 6s;
            }
          }

          @keyframes bubble{
            0%{}
            25%{
              border-radius: 45% 55% 58% 42% / 30% 49% 51% 70%;
            }

            50%{
              transform:translateY(-46px);
              border-radius: 36% 64% 29% 71% / 63% 71% 29% 37%;
            }
            75%{
              border-radius: 29% 71% 44% 56% / 32% 49% 51% 68%;
            }
          }
        }
      }
    }
  }


// COPD CSS
