import * as d3 from 'd3'
import '../styles/main.scss'

export { transformData, calcMaximumAverage, calcAverage, getAvg }

// d3.json('./data/amsterdam_NO2_20190101.json').then(data => {
//   console.log('raw data: ', data)
//   let transformedData = transformData(data)
//   console.log('transformedData: ', transformedData)

//   let maximumAverageDay = calcMaximumAverage(transformedData)
//   console.log('maximumAverage: ', maximumAverageDay)

//   let dayAverage = calcDayAverage(transformedData)
//   console.log('dayAverage: ', dayAverage)
// })

Promise.all([
  d3.json('./data/amsterdam_NO2_20190101.json'),
  d3.json('./data/amsterdam_NO2_20190102.json'),
  d3.json('./data/amsterdam_NO2_20190103.json'),
  d3.json('./data/amsterdam_NO2_20190104.json'),
  d3.json('./data/amsterdam_NO2_20190105.json'),
  d3.json('./data/amsterdam_NO2_20190106.json'),
  d3.json('./data/amsterdam_NO2_20190107.json')
]).then((data) => {
  // data.forEach(dataDay => { console.log('raw data: ', dataDay) })

  let transformedData = transformData(data)
  console.log('all data transformed: ', transformedData)

  console.log('zou true moeten zijn: ', transformedData instanceof Array)
  console.log('zou false moeten zijn: ', transformedData[0])


  let weekAverage = calcAverage(transformedData)
  console.log('weekAverage: ', weekAverage)

  let dayAverage = calcAverage(transformedData[0])
  console.log('dayAverage: ', dayAverage)

}).catch((err) => {
  console.log('Error loading data!, ', err)
})

function transformData (data) {
  let transformedDataArray = []

  data.forEach(dataItem => {
    const chunck = 42128

    let concChunkArray = []

    for (let i = 1; i <= 24; i++) {
      let concChunk = dataItem.conc_ana.slice(i - 1, chunck * i)
      concChunkArray.push(concChunk)
    }

    console.log(dataItem)
    let day = []

    for (let i = 1; i <= 24; i++) {
      let hour = {
        'id': i,
        'time': dataItem.time[i - 1],
        'temperature': dataItem.temperature[i - 1],
        'uWind': dataItem.uWind[i - 1],
        'vWind': dataItem.vWind[i - 1],
        'dataArray': []
      }

      dataItem.lon.forEach((item, index) => {
        if (index % 100 === 0) {
          let obj = {
            'id': (index / 100) + 1,
            'long': item,
            'lat': dataItem.lat[index],
            'concAna': concChunkArray[i - 1][index]
          }
          hour.dataArray.push(obj)
        }
      })

      day.push(hour)
    }

    transformedDataArray.push(day)
  })

  return transformedDataArray
}

function calcMaximumAverage (transformedData) {
  // transformedData[0].forEach(hour => {
  //   console.log('hour: ', hour)
  //   let maxValue = Math.max.apply(Math, hour.dataArray.map(function(o) { return o.concAna }))
  //   let index = hour.dataArray.indexOf(maxValue)
  //   console.log('value: ', maxValue)
  // })

  let maximumArray = transformedData[0].map(hour => {
    return hour.dataArray[359].concAna
  })

  return getAvg(maximumArray)
}

function calcAverage (transformedData) {
  let averagePerHour = []

  if (transformedData.length === 24) {
    transformedData.forEach(hour => {
      let hourConcAna = hour.dataArray.map(item => {
        return item.concAna
      })
      let hourConcAnaAverage = getAvg(hourConcAna)
      averagePerHour.push(hourConcAnaAverage)
    })
  } else {
    transformedData.forEach(dayItem => {
      dayItem.forEach(hour => {
        let hourConcAna = hour.dataArray.map(item => {
          return item.concAna
        })
        let hourConcAnaAverage = getAvg(hourConcAna)
        averagePerHour.push(hourConcAnaAverage)
      })
    })
  }
  let totalAverage = getAvg(averagePerHour)
  return totalAverage
}

function getAvg (array) {
  const total = array.reduce((acc, c) => acc + c, 0)
  return total / array.length
}
